import { Component, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { leaveFunctions } from "./leaveMainFunction";
import "jquery-ui/ui/widgets/datepicker";
import "jquery-ui/themes/base/all.css";

export class holidays extends Component {
  constructor(props) {
    super(props);

    this.refreshlist = this.refreshlist.bind(this);

    this.state = {
      blockedDates: ["2023-05-14", "2023-05-20", "2023-05-25"],
      userData: [],
      agmUnit: "",
      dgmUnit: "",
      serviceUnit: "",
      username: "",
    };
  }

  componentDidMount() {
    this.refreshlist();
  }

  refreshlist() {}

  render() {
    const { blockedDates, userData, agmUnit, dgmUnit, serviceUnit, username } =
      this.state;

    return (
      <div>
        <h1>Leave Management System</h1>

        <section class="py-4 py-xl-5">
          <div class="container">
            <div class="text-white bg-dark border rounded border-0 p-4 p-md-5">
              <h2 class="fw-bold text-white mb-3">
                Bureau Holidays and Mercantile Holidays
              </h2>
              <div class="my-3"></div>
              <div class="table-responsive" style="border-style: none;">
                <table class="table">
                  <thead style="border-style: none;">
                    <tr style="border-style: none;">
                      <th style="border-style: none;">
                        <select>
                          <optgroup label="This is a group">
                            <option value="12" selected="">
                              This is item 1
                            </option>
                            <option value="13">This is item 2</option>
                            <option value="14">This is item 3</option>
                          </optgroup>
                        </select>
                        Column 1
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>
                        <textarea></textarea>Cell 1
                      </td>
                      <td>Text</td>
                      <td>Cell 2</td>
                    </tr>
                    <tr></tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}
